import { FC, ReactNode, createContext, useMemo, useState } from "react";
import { IGameBoardWord } from "../types";

type IGameBoardWords = IGameBoardWord[];

export interface IGameBoardContext {
  words: IGameBoardWords;
  updateWords: (words: IGameBoardWords) => void;
}

type TGameBoardContext = IGameBoardContext | null;

export const GameBoardContext = createContext<TGameBoardContext>(null);

const useGameBoardContext = (initialWords: IGameBoardWords = []) => {
  const [words, setWords] = useState<IGameBoardWords>(initialWords);

  return {
    words,
    setWords,
  };
};

export const GameBoardContextProvider: FC<{ children: ReactNode }> = ({
  children,
}) => {
  const [words, setWords] = useState<IGameBoardWords>([]);

  const updateWords = (newWords: IGameBoardWords) => setWords(newWords);

  const value = useMemo(() => ({ words, updateWords }), [words]);

  return (
    <GameBoardContext.Provider value={value}>
      {children}
    </GameBoardContext.Provider>
  );
};
