import styled from "styled-components";
import { IGameBoardWord } from "../types";
import { FC, useContext } from "react";
import { BoardWord } from "../BoardWord";
import { GameBoardContext } from "../GameBoardContext";

interface IProps {
  words: IGameBoardWord[];
}

const StyledBoardLine = styled.div`
  display: grid;
  grid-template-rows: repeat(4) auto;
  row-gap: 1rem;
`;

export const BoardLine: FC<IProps> = ({ words }) => {
  const context = useContext(GameBoardContext);

  return (
    <StyledBoardLine>
      {words.map((word) => (
        <BoardWord boardWord={word} />
      ))}
    </StyledBoardLine>
  );
};
