import styled from "styled-components";
import { IBaseProps } from "../types";
import { FC, RefObject, useState } from "react";
import { AnimatedProps, animated } from "@react-spring/web";
import { colorPick } from "../color";

interface IProps extends IBaseProps {
  action?: () => void;
  forwardRef?: RefObject<HTMLButtonElement>;
  springs?: AnimatedProps<any>;
}

interface IStyledButtonProps {
  $isMouseDown: boolean;
}

const StyledButtonBackground = styled.div`
  background-color: black;
  width: 20rem;
`;

const StyledButton = styled(animated.button)<IStyledButtonProps>`
  position: absolute;
  padding: 1rem;
  margin: 1rem;
  color: ${colorPick.text};
  font-weight: 600;
  font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
  background-color: ${(props) =>
    props.$isMouseDown ? colorPick.primary : colorPick.primary};
  user-select: none;
  border: 1px solid
    linear-gradient(
      315deg,
      rgba(0, 0, 0, 1) 0%,
      rgba(41, 41, 47, 1) 50%,
      rgba(255, 255, 255, 1) 100%
    );
`;

export const Button: FC<IProps> = ({
  action,
  forwardRef,
  children,
  springs,
  ...restProps
}) => {
  const [isMouseDown, setIsMouseDown] = useState(false);

  const handleMouseUp = () => {
    setIsMouseDown(false);
    action && action();
  };

  return <StyledButtonBackground></StyledButtonBackground>;
};
