import { useEffect, useRef, useState } from "react";
import { Button } from "../../ui/Button";
import { useSpring } from "@react-spring/web";
import { useGameBoardContext } from "../GameBoardContext";

export const SubmitSlider = () => {
  const parentRef = useRef<HTMLDivElement>(null);
  const buttonRef = useRef<HTMLButtonElement>(null);

  const { checkIsCorrectWords, wrongAnswerAmount } = useGameBoardContext();

  const [parentWidth, setParentWidth] = useState(0);
  const [buttonWidth, setButtonWidth] = useState(0);

  useEffect(() => {
    if (parentRef.current) {
      setParentWidth(parentRef.current.offsetWidth);
    }
    if (buttonRef.current) {
      setButtonWidth(buttonRef.current.offsetWidth);
    }
  }, []);

  const [buttonPosition, setButtonPosition] = useState(0);

  const [springs, api] = useSpring(() => ({
    from: { x: buttonPosition },
  }));

  const handleButtonMove = () => {
    console.log({ wrongAnswerAmount });
    const newButtonPosition =
      buttonPosition === 0 ? parentWidth / wrongAnswerAmount - buttonWidth : 0;

    api.start({
      from: {
        x: buttonPosition,
      },
      to: {
        x: newButtonPosition,
      },
    });

    setButtonPosition(newButtonPosition);
  };

  const handleCheckResult = () => {
    handleButtonMove();
    checkIsCorrectWords();
  };

  return (
    <div
      ref={parentRef}
      style={{
        background: "red",
      }}
    >
      <Button
        action={handleCheckResult}
        forwardRef={buttonRef}
        springs={springs}
      >
        Click me
      </Button>
    </div>
  );
};
