import { useState } from "react";
import { IGameBoardWord } from "../types";
import constate from "constate";

const useGameBoard = (initialWords: IGameBoardWord[] = []) => {
  const [words, setWords] = useState<IGameBoardWord[]>(initialWords);

  const updateWords = (newWords: IGameBoardWord[]) => setWords(newWords);

  return {
    words,
    updateWords,
  };
};

const [GameBoardContextProvider, useGameBoardCtx] = constate(useGameBoard);

export const GameBoardProvider = () => {};

export const useGameBoardContext = () => {
  const { updateWords, words } = useGameBoardCtx();

  return {
    updateWords,
    words,
  };
};
