import { FC, ReactNode, createContext, useState } from "react";
import { IGameBoardWord } from "../types";

type IGameBoardWords = IGameBoardWord[];

interface IGameBoardContext {
  words: IGameBoardWords;
  setWords: (words: IGameBoardWords) => void;
}

// rename
// type TGameBoardContext = string;
type TGameBoardContext = IGameBoardContext | null;

export const GameBoardContext = createContext<TGameBoardContext>(null);

export const GameBoardContextProvider: FC<{ children: ReactNode }> = ({
  children,
}) => {
  const [words, setWords] = useState([]);

  return (
    <GameBoardContext.Provider value={{ words, setWords }}>
      {children}
    </GameBoardContext.Provider>
  );
};
