import styled from "styled-components";
import { IGameBoardWord } from "../types";
import { FC, useContext } from "react";
import { BoardWord } from "../BoardWord";

interface IProps {
  words: IGameBoardWord[];
  gridColumn?: number;
}

const StyledBoardLine = styled.div<{ $gridColumn?: number }>`
  display: grid;
  grid-column: ${(props) => props.$gridColumn};
`;

export const BoardLine: FC<IProps> = ({ words, gridColumn }) => {
  return (
    <StyledBoardLine $gridColumn={gridColumn}>
      {words.map((word, i) => (
        <BoardWord key={word.word} boardWord={word} gridRow={i} />
      ))}
    </StyledBoardLine>
  );
};
