import { FC } from "react";
import { IGameBoardWord } from "../types";
import styled from "styled-components";
import { BoardLine } from "../BoardLine";
import { BoardWord } from "../BoardWord";

const StyledGameBoard = styled.div`
  display: grid;
  grid-template: repeat(4, 1fr) / repeat(4, 1fr);
  grid-auto-flow: row;
`;

interface IProps {
  wordColumns: IGameBoardWord[][];
}

export const WordGrid: FC<IProps> = ({ wordColumns }) => (
  <StyledGameBoard>
    {/* {wordColumns.map((line, i) => (
      <BoardLine key={i} words={line} />
    ))} */}
    {wordColumns.flat().map((word) => (
      <BoardWord key={word.word} boardWord={word} />
    ))}
  </StyledGameBoard>
);
