import { FC } from "react";
import { IGameBoardWord } from "../types";
import * as R from "ramda";
import { BoardLine } from "../BoardLine";
import styled from "styled-components";
import { useGameBoardContext } from "../GameBoardContext";

interface IProps {}

const StyledGameBoard = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr) auto;
  column-gap: 1rem;
`;

export const GameBoard: FC<IProps> = () => {
  const { words, setWords } = useGameBoardContext();

  const boardLines = R.splitEvery(4, words);

  return (
    <StyledGameBoard>
      {boardLines.map((line) => (
        <BoardLine words={line} />
      ))}
    </StyledGameBoard>
  );
};
