import { FC } from "react";
import { IGameBoardWord } from "../types";
import * as R from "ramda";
import { BoardLine } from "../BoardLine";
import styled from "styled-components";

interface IProps {
  boardWords: IGameBoardWord[];
}

const StyledGameBoard = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr) auto;
  column-gap: 1rem;
`;

export const GameBoard: FC<IProps> = ({ boardWords }) => {
  const { words } = useGameBoardContext();

  console.log({ words });

  const boardLines = R.splitEvery(4, boardWords);
  return (
    <StyledGameBoard>
      {boardLines.map((line) => (
        <BoardLine words={line} />
      ))}
    </StyledGameBoard>
  );
};
