    word: "cotton",
  },
  {
    categoryId: "soft_items",
    word: "silk",
  },
  {
    categoryId: "drinks",
    word: "juice",
  },
  {
    categoryId: "drinks",
    word: "milk",
  },
  {
    categoryId: "drinks",
    word: "wine",
  },
  {
    categoryId: "drinks",
    word: "beer",
  },
];

export const Game: FC<IProps> = () => {
  const [text, setText] = useState("");

  return (
    <GameBoardContext.Provider value={{ text, setText }}>
      <GameBoard boardWords={dummyWords} />;
    </GameBoardContext.Provider>
  );
};
