import styled from "styled-components";
import { IBaseProps } from "../types";
import { FC, RefObject, useEffect, useRef, useState } from "react";
import { animated, useSpring } from "@react-spring/web";

interface IProps extends IBaseProps {
  action?: () => void;
  forwardRef?: RefObject<HTMLButtonElement>;
}

interface IStyledButtonProps {
  $isMouseDown: boolean;
}

const StyledButton = styled(animated.button)<IStyledButtonProps>`
  padding: 1rem;
  color: #ff0000;
  font-weight: 600;
  font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
  background-color: ${(props) => (props.$isMouseDown ? "#c5c57e" : "#ffff00")};
  border: 1px solid #cfcf00;
  border-radius: 0.5rem;
`;

export const Button: FC<IProps> = ({
  action,
  forwardRef,
  children,
  ...restProps
}) => {
  const [isMouseDown, setIsMouseDown] = useState(false);
  const [parentWidth, setParentWidth] = useState(0);
  const [buttonWidth, setButtonWidth] = useState(0);
  const parentRef = useRef<HTMLDivElement>(null);
  const buttonRef = useRef<HTMLButtonElement>(null);

  useEffect(() => {
    if (parentRef.current) {
      setParentWidth(parentRef.current.offsetWidth);
    }
    if (buttonRef.current) {
      setButtonWidth(buttonRef.current.offsetWidth);
    }
  }, []);

  const [buttonPosition, setButtonPosition] = useState(0);

  const [springs, api] = useSpring(() => ({
    from: { x: buttonPosition },
  }));

  // use this for toggles if needed! cool trick
  // could also be added (prolly should) to the useSpring config!
  const handleButtonMove = () => {
    console.log({ parentWidth });

    const newButtonPosition =
      buttonPosition === 0 ? parentWidth - buttonWidth : 0;
    api.start({
      from: {
        x: buttonPosition,
      },
      to: {
        x: newButtonPosition,
      },
    });
    setButtonPosition(newButtonPosition);
  };

  const handleMouseUp = () => {
    handleButtonMove();
    setIsMouseDown(false);
    action && action();
  };
  return (
    <div
      ref={parentRef}
      style={{
        background: "red",
      }}
    >
      <StyledButton
        $isMouseDown={isMouseDown}
        onMouseDown={() => setIsMouseDown(true)}
        onMouseUp={handleMouseUp}
        style={{
          ...springs,
        }}
        ref={forwardRef}
        {...restProps}
      >
        {children}
      </StyledButton>
    </div>
  );
};
