import styled from "styled-components";
import { useGameBoardContext } from "../GameBoardContext";

const StyledWrongAnswerMessageContainer = styled.div`
  margin: 1rem;
  font
`;

export const WrongAnswerMessage = () => {
  const { wrongAnswerMessageVisible } = useGameBoardContext();

  if (!wrongAnswerMessageVisible) {
    return null;
  }

  return (
    <StyledWrongAnswerMessageContainer>Wrong</StyledWrongAnswerMessageContainer>
  );
};
