import { FC } from "react";
import { IGameBoardWord } from "../types";
import { BoardWord } from "../BoardWord";
import * as R from "ramda";

interface IProps {
  boardWords: IGameBoardWord[];
}

export const GameBoard: FC<IProps> = ({ boardWords }) => {
  const boardLines = R.splitEvery(4, boardWords);

  return (
    <>
      {}

      {boardWords.map((word) => (
        <BoardWord boardWord={word} />
      ))}
    </>
  );
};
